 Set @SpResponseMessage='GenericError';               
 Set @SpErrorDetails ='ErrorNumber : <' +convert(varchar(500),
 Error_number())+ '>, ErrorSeverity : <'+ convert(varchar(500),Error_severity())+'>, 
