				insert into coc_ServiceTask ( TaskTypeId, Date, ExecuteDate, UniqueId, Parameters, UserId, Status, RowStatus )
				select 1
				,      getdate()
				,      getdate()
				,      @EServiceID
				,      @xmldata
				,      @UserID
				,      1
				,      1
			end

