 @UserID INTEGER,     
 @LanguageID INT=1,     
 @RoleID BIGINT=0,     
 @ImpersonationUserID BIGINT=0,   
 @SpResponseStatus INT output,     
 @SpResponseMessage NVARCHAR(max) output,     
 @SpErrorDetails NVARCHAR(max) output 
AS  
BEGIN  
  begin try
  begin transaction deleteimage
 SET NOCOUNT ON;  
  
  DECLARE @NxtPrimaryImage INTEGER = 0;  
  
  BEGIN  
     
   IF(@IsPrimaryImage = 1 )  
