    UPDATE  
     coc_EVoucherImages SET  
     IsPrimaryImage    = 1  
    WHERE  
     EVoucherImagesID   = @NxtPrimaryImage  
  
   END  
     
  END  
  SET @SpResponseStatus = 1;  
  commit transaction deleteimage
  end Try
 BEGIN catch     
