    --Check if competition is eligible in the given RetailOutlet.
    IF NOT EXISTS(SELECT RetailOutletID FROM coc_ECouponOutlets where ECouponID = @ECouponID AND RetailOutletID = @RetailoutletID AND IsActive = 1)
