    select @NotificationID = NotificationID from coc_notifications(NOLOCK) where eserviceid = @EServiceID                                                
             
 INSERT INTO @TempBarcodeTable (                                                  
 Barcode                                  
 ,EServiceBarcodeID                                                 
 ,NotificationID)                          
