   ,null                                   
   ,0                                      
   ,null                              
   ,null                              
   ,null            
   ,@ExternalBarcode          
     FROM @TempBarcodeTable                                                  
    SELECT top 1 @ParticipationBarcode = Barcode FROM @TempBarcodeTable                                              
