    SET @SpResponseMessage='GenericError'; 
    SET @SpErrorDetails ='ErrorNumber : <' 
                         + CONVERT(NVARCHAR(20), Error_number()) 
                         + '>,                  ErrorSeverity : <' 
                         + CONVERT(NVARCHAR(20), Error_severity()) 
                         + '>,                   ErrorState : <' 
