    end    
    else    
    begin    
       UPDATE coc_BarcodesConsumption SET CompetitionStagingId = @CompetitionStagingID,NoOfBarcodesUsed=@NoOfCoupons where  BarcodeConsumptionID=@BarcodeConsumptionID     
    
