					FROM @Winners EC 
					WHERE EC.ECouponWinnerID=@Counter	
		
					
		SELECT @Counter=MIN(ECouponWinnerID) FROM @Winners WHERE ECouponWinnerID>@Counter
		END	
		
			--Tables[3] 
		SELECT  DISTINCT * FROM @WinnerList ORDER BY ECouponID DESC 

	END
