  --Tables[1] Ticket    
  SELECT TC.EServiceEventID,TC.EServiceID,TC.EventDescription,EV.EnumValue,FORMAT(TC.EventDate,'dd/MM/yyyy')EventDate,FORMAT(TC.LastDate,'dd/MM/yyyy')LastDate,SL.EService [ServiceName],    
