   INSERT INTO @WinnerList    
    SELECT TOP 2 EC.ECouponID,CS.GuestName [WinnerName],CP.EnumValue [PrizeType],EP.PrizeHeader,EP.PrizeDetail,'' ImageFile ,SR.EService,0 ConsumerID    
     FROM @Winners EC     
