    SELECT EV.EnumValueID,EnumID,BaseValue,EL.EnumValue    
     FROM coc_EnumValues  EV(NOLOCK)    
      LEFT JOIN coc_EnumValueLanguages EL (NOLOCK) ON EV.EnumValueID=EL.EnumValueID AND LanguageID=@LanguageID    
