    SELECT @Counter = MIN(Sno) FROM @Resources WHERE Sno>@Counter    
    
    SET @ResourceValue=NULL    
    SET @ResourceID=NULL    
   END    
   END     
  IF(@Type=4)    
  BEGIN     
