 DECLARE @FirstRecord INTEGER                  
 DECLARE @LastRecord INTEGER                  
                  
 SET @FirstRecord = (@PageNumber - 1) * @PageSize                  
 SET @LastRecord = (@PageNumber * @PageSize + 1)                  
                  
DECLARE @ResultTable AS TABLE                  
(                  
[Id] INTEGER Identity (1,1),                  
