RIGHT(CONVERT(VARCHAR(30),(ev.ExpiryDate), 22), 11) as Time,          
 cns.MembershipNumber AS [MembershipID],                            
 es.CorporateID,                            
 crs.CorporateName ,                          
 kll.KioskLocation,                          
 rol.RetailOutlet,            
 CASE                            
  WHEN CONVERT(INT,esb.IsUsed)=1                            
