CREATE PROCEDURE [dbo].[ImportCorporateUserData]  
	@XmlData XML,	 
	@UserID INTEGER,
	--@CorporateID INTEGER,
	@ErrorMessage [Remark] OUTPUT 
AS 
	SET NOCOUNT ON
	BEGIN TRY
	BEGIN TRANSACTION TRN_SaveCourier
 		DECLARE @CorporateUser AS TABLE 
