	@CountryID INTEGER,
	@CityID INTEGER, 
	@DynamicQuery XML,
	@IsActive BIT=NULL,
	@IsIntigration BIT=NULL,
	@ReturnValue [Remark] OUTPUT
AS
SET NOCOUNT ON
