			DELETE FROM coc_CityLanguages WHERE CityID=@CityID
			INSERT INTO coc_CityLanguages (CityID,LanguageID,City)
				SELECT @CityID,LanguageID,Value FROM @Value
		END
		SET @ReturnValue ='' 
