CREATE PROCEDURE [dbo].[SaveContactChanges]
	@UserID INTEGER,
	@EmailID [Email]=NULL,
	@MobileNo [Mobile]=NULL,
	@TypeID INTEGER,
	@ErrorMessage [Remark] OUTPUT 
AS
SET NOCOUNT ON
	BEGIN TRY 
	DECLARE @ConsumerID INTEGER
	 BEGIN TRANSACTION TRN_SaveConsumers

		SELECT @ConsumerID=ISNULL(ConsumerID ,0)
			FROM coc_Consumers(NOLOCK) 
		WHERE UserID=@UserID

	 	IF (ISNULL(@ConsumerID,0) > 0 )
		BEGIN
			 IF @TypeID=1
			 BEGIN
				UPDATE COC_Consumers 
					SET EmailID=@EmailID
					FROM COC_Consumers 
				WHERE ConsumerID = @ConsumerID

				UPDATE coc_Users 
					SET EmailID=@EmailID,UserName=@EmailID
					FROM coc_Users 
				WHERE UserID = @UserID
			END
			ELSE
			BEGIN
				UPDATE COC_Consumers 
					SET PrimaryMobile=@MobileNo
					--,SecondaryMobile =@MobileNo
					--FROM COC_Consumers 
				WHERE ConsumerID = @ConsumerID

				UPDATE coc_Users 
					SET MobileNumber=@MobileNo,
						PhoneNumber=@MobileNo
					FROM coc_Users 
				WHERE UserID = @UserID
			END
		END
		ELSE BEGIN
			IF (@TypeID =1)
			BEGIN
				UPDATE coc_Users 
					SET EmailID=@EmailID,UserName=@EmailID
					FROM coc_Users 
				WHERE UserID = @UserID
			END
			ELSE
			BEGIN
				UPDATE coc_Users 
					SET MobileNumber=@MobileNo,
						PhoneNumber=@MobileNo
					FROM coc_Users 
				WHERE UserID = @UserID
			END
		END
 		--SET @ErrorMessage = CONVERT(NVARCHAR,@ConsumerID)
	 COMMIT TRANSACTION TRN_SaveConsumers
	END TRY
	BEGIN CATCH									
		SELECT @ErrorMessage=ERROR_MESSAGE()
		RAISERROR(@ErrorMessage, 16, 1);			
	END CATCH

SET NOCOUNT OFF
