     ,languageid  
     ,resourcekey  
     ,value  
     ,Location  
     ,UsesTerm  
     )  
    SELECT @ECouponID  
     ,2  
     ,'Description'  
     ,ED.valueAr  
     ,ED.LocationAr  
     ,'<div dir= ''rtl'' >' + @UsesTermAR + '</div>'  
    FROM @LanguageResources ED  
  
    SET @ReturnValue = @ECouponID  
   END  
  END  
  
  COMMIT TRANSACTION saveecouponconfig  
 END TRY  
  
 BEGIN CATCH  
