					SELECT @LanguageCounter = MIN(LanguageID) FROM @QuestionBanks WHERE QuestionNo=@QuestionNo AND LanguageID>@LanguageCounter
				END

				INSERT INTO coc_QuestionOptions(QuestionBankID,IsCorrect)
