     ,@Email                            
        
     ,@ConsumerID                            
        
     ,'P'                            
        
     ,@ConsumerName                            
        
    FROM @TempBarcodeTable                            
        
    select * from @TempBarcodeTable;           
        
    SET @ErrorMessage = 'You Already Participated'                            
        
