      ,@AnswerRank = AnswerRank                            
        
      ,@AnswerValue = AnswerValue                            
        
     FROM @Answers                            
        
                            
        
     DECLARE @ActiveID INTEGER                            
        
                            
        
     WHILE @ID IS NOT NULL                       
        
      AND @ID <> ISNULL(@ActiveID, 0)                            
        
     BEGIN                            
        
      SET @ActiveID = @ID                            
        
                            
        
