    END              
                  
   END                       
   ELSE                    
   BEGIN                    
    SET @ErrorMessage = 'BarcodeAlreadyUsedORInvalid' ---'Barcode is Already Used OR Invalid Barcode'                                 
   END                    
                    
   IF @ErrorMessage = ''                    
   BEGIN                    
    -------------------------------------------------------------------//                                
