					SELECT @RetailOutletID,@LanguageID,@RetailOutlet 
			END
			SELECT @Counter=MIN(UniqueID) FROM @Value WHERE UniqueID>@Counter
		END
		SET @ReturnValue=''
		
		  
	SET NOCOUNT OFF
