   FROM coc_Guests(NOLOCK)                                      
                  
   WHERE GuestID = @GuestID                                      
                  
    AND IsActive = 1                                      
                  
    AND IsVerified = 1                                      
                  
                                      
                  
  IF @Mobile IS NULL                                      
                  
   SELECT @Mobile = PrimaryMobile                                      
                  
   FROM coc_Guests(NOLOCK)         
                  
   WHERE GuestID = @GuestID                                      
                  
    AND IsActive = 1                                      
                  
    AND IsVerified = 1                                      
                  
 END                                      
                  
                                      
                  
 IF @Mobile IS NULL                                      
                  
  OR len(@Mobile) <> 14                                      
                  
  OR SUBSTRING(@Mobile, 1, 6) <> '009665'                                      
                  
 BEGIN                                      
                  
