  DECLARE @AllowMultiple bit                                      
                  
  DECLARE @GeneralNotificationTemplateID INT                                      
                  
                                      
                  
  SELECT @EServiceID = EServiceID                                      
                  
   ,@NumberOfConsumerParticipations = ISNULL(NumberOfConsumerParticipations, 0)                                      
                  
