       WHERE  (ParticipationEmailID= @Email   or ParticipationMobile=@Mobile )              
        AND ECouponID = @EcouponID  and (@RetailoutletID is null or RetailOutletID=@RetailoutletID)                                    
        IF( @ParticipationsCount >= 1)           
         BEGIN          
   SET @SPStatus = 1;                                      
                
