    )                                      
                  
  BEGIN                                      
                  
   DECLARE @Participations INT                                      
                  
   DECLARE @NoOfCoponsLeft INT;                
             
   --Same Mobile and Email Condition start          
                                   
                  
     IF(ISNULL(@RetailoutletID,0) > 0) --retail outlet condition            
    BEGIN            
       Declare @TotalAllowedParticipationinOutlet int;            
