  END                                      
                  
 ELSE -- else entrytypeid not in 41 or 42                                      
                  
  BEGIN                                      
                  
   select * from @TempBarcodeTable;                                      
                  
   SET @ErrorMessage = 'Invalid Competition Type'                                      
                  
   SET @SPStatus = 1;                                      
                  
   SET @SPReturnMessage = @ErrorMessage;                                  
                  
