  IF (ISNULL(@ErrorMessage, '') <> '')                                      
                  
  BEGIN                                      
                  
   IF @ConsumerID IS NOT NULL                                      
                  
   BEGIN                  -------------------------------------------------------------------//                                                 
                  
    --//-------------------------------------------------------                                                    
                  
