     ,@EServiceID = @EServiceID                                      
                  
      
                  
    PRINT '@return_value1'                                      
                  
    PRINT @return_value1                                      
                  
                                      
                  
    --------------------------------------------------------------------//                                                    
                  
    SET XACT_ABORT ON;                                      
                  
   END                                      
                  
  END                                      
                  
                                      
                  
  SELECT @ErrorMessage                                      
                  
                                      
                  
                                    
                  
 END                                      
                  
                             
                  
                                    
                  
 COMMIT TRANSACTION SaveECouponConfiguration                                      
                  
END TRY                                      
                  
                                      
                  
BEGIN CATCH                                      
                  
 ROLLBACK TRANSACTION SaveECouponConfiguration                                      
                  
                                      
                  
 SELECT @ErrorMessage = ERROR_MESSAGE()                                      
                  
                                      
                  
 SET @SPStatus = 1;                                      
                  
