 LEFT JOIN [coc_Consumers] CS(NOLOCK) ON CS.UserID = US.UserID  
 LEFT JOIN [coc_CorporateUsers] CU(NOLOCK) ON CU.UserID = US.UserID  
 LEFT JOIN [coc_CityLanguages] CL(NOLOCK) ON CS.CityID = CL.CityID  
